public static void main(Args _args)
{
    SC_PrintBarcodeController controller = SC_PrintBarcodeController::construct(_args);
    controller.startOperation();
}

public static SC_PrintBarcodeController construct(Args _args)
{
    SC_PrintBarcodeController controller = new SC_PrintBarcodeController();
    controller.parmReportName(ssrsReportStr(SC_AssetBarcode,PrecisionDesign1));
    controller.parmArgs(_args);
    controller.parmLoadFromSysLastValue(false);
    controller.parmDialogCaption("@SC_AseetBarcodeLabels:ReportCaption");
    if(_args && _args.dataset() == tableNum(AssetTable))
    {
        SC_PrintBarcodeContract contract = controller.parmReportContract().parmRdpContract() as SC_PrintBarcodeContract;
        if (contract)
        {
            AssetTable assetTable = controller.parmArgs().record() ;
            contract.parmAssetId(assetTable.AssetId);
        }
    }
    return controller;
}
