SourceCode.Declaration:
[SRSReportParameterAttribute(classStr(SC_PrintBarcodeContract))]
internal final class SC_PrintBarcodeDP extends SRSReportDataProviderBase
{
    SC_AssetBarcodeTmp assetBarCodeTmp;

}

Method.Source:
[SRSReportDataSetAttribute(tableStr(SC_AssetBarcodeTmp))]
public SC_AssetBarcodeTmp getAssetBarcodeTmp()
{
    select assetBarCodeTmp;
    return assetBarCodeTmp;
}

    public void processReport()
    {
        AssetId assetId;
        int counts, i;
        SC_PrintBarcodeContract contract;

        contract = this.parmDataContract() as SC_PrintBarcodeContract;

        assetId = contract.parmAssetId();
        counts  = contract.parmCounts();

        if (!assetId)
        return;

        if (counts <= 0)
            counts = 1; 
        
        ttsbegin;
        delete_from assetBarCodeTmp;

        for (i = 1; i <= counts; i++)
        {
            assetBarCodeTmp.clear();
            assetBarCodeTmp.AssetId = assetId;
            assetBarCodeTmp.CountNo = i;
            assetBarCodeTmp.insert();
        }
        ttscommit;
    }
